import React, { useReducer } from "react";
import CartContext from "./cart-contenxt";

const cartInitialState = {
  items: [],
  totalAmount: 0,
};
const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.item.id,
    );
    const existingCartItem = state.items[existingCartItemIndex];
    let updatedItems;
    if (existingCartItem) {
      // Item already exists in the cart, update its amount
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = {
        ...existingCartItem,
        amount: existingCartItem.amount + action.item.amount,
      };
    } else {
      // Item is not in the cart, add it as a new entry
      updatedItems = [...state.items, action.item];
    }
    return {
      totalAmount: state.totalAmount + action.item.price * action.item.amount,
      items: updatedItems,
    };
  }
  if (action.type === "REMOVE") {
    // Implement the logic to remove an item from the cart (if needed)
    // Since it's not provided in the original code, we'll return the same state for now
    return state;
  }
  return cartInitialState;
};

const CartProvider = (props) => {
  const [cartState, cartDispatch] = useReducer(cartReducer, cartInitialState);

  const addItemtoCartHandler = (item) => {
    cartDispatch({
      type: "ADD",
      item,
    });
  };
  const removeItemFromCartHandler = (id) => {
    cartDispatch({
      type: "REMOVE",
      id,
    });
  };
  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemtoCartHandler,
    removeItem: removeItemFromCartHandler,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
